import axios from "axios"

const BASE_URL = "http://localhost:5000/api/";

// const user = JSON.parse(localStorage.getItem("persist:root"))?.user;
// const currentUser = user && JSON.parse(user).currentUser;
// const TOKEN = currentUser?.accessToken;

const TOKEN = "eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJpZCI6IjY1OWEzZDlkNWM4N2ExM2E0Yzc2OTQ1NyIsImlzQWRtaW4iOnRydWUsImlhdCI6MTcwNDc5ODMwNSwiZXhwIjoxNzA1MDU3NTA1fQ.ZN_hLiiH1WZc_glPV-W8UZ1CGlY442zX7ldoUClnhfg"

export const publicRequest = axios.create({
    baseURL: BASE_URL,
});

export const userRequest = axios.create({
    baseURL: BASE_URL,
    header: { token: `Bearer ${TOKEN}` },
});
